.cheque-container {
	background: url(/files/cheque_empty.png) no-repeat;
	background-size: contain;
	width: 8.5in !important;
	height: 11in !important;
}

.cheque-date td {
	width:0.15in;
	text-align:center;
}
.cheque-date tfoot td {
	padding-top:0;
	padding-bottom:0;
	font-size:7.5pt;
}

.print-format {
	padding: 0;
	max-width: 8.5in !important;
	min-height: 11in !important;
}
.print-preview {
	padding: 0;
	max-width: 8.5in !important;
	min-height: 11in !important;
}
.print-format td {
	padding: 0.01in !important;
}
